import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Container, Row, Col, Form, FormGroup, Label, Input } from 'reactstrap';



const ModalBtn = (props) => (
    <div>
        <Button  onClick={props.handleToggle} className={`rounded-circle hourBtn booked-${props.status}`} data-name={props.data}>{props.buttonLabel}</Button>
        <Modal isOpen={props.modalState} toggle={props.handleToggle} className="modal-dialog-centered">
            <ModalHeader toggle={props.handleToggle}>Schedule Appointment</ModalHeader>
            <ModalBody>
                <Form>
                    <FormGroup row>
                        <Label for="exampleFullName" sm={1}><i className="fa fa-user prefix"></i></Label>
                        <Col sm={11}>
                            <Input type="text" name={props.name} id="exampleFullName" placeholder="Full Name" onChange={props.changeEvent} value={props.FullName} />
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="examplePhone" sm={1}><i className="fas fa-phone"></i></Label>
                        <Col sm={11}>
                            <Input type="text" name={props.phone} id="examplePhone" placeholder="Phone Number" onChange={props.changeEvent} value={props.PhoneNumb} />
                        </Col>
                    </FormGroup>
                </Form>
            </ModalBody>
            <ModalFooter>
                <Button  data-class={props.dataN} color="primary" onClick={props.Submit}>Submit</Button>
                <Button color="secondary" onClick={props.handleToggle}>Cancel</Button>
            </ModalFooter>
        </Modal>

    </div>

)

