import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Container, Row, Col, Form, FormGroup, Label, Input } from 'reactstrap';



const ModalBtn = (props) => (
    <div>
        <Button  onClick={props.handleToggle} className={`rounded-circle hourBtn booked-${props.status}`}>{props.buttonLabel} </Button>
        <Modal isOpen={props.modalState} toggle={props.handleToggle} className="modal-dialog-centered">
            <ModalHeader toggle={props.handleToggle}>Schedule Appointment</ModalHeader>
            <ModalBody>
                <Form>
                    <FormGroup row>
                        <Label for="exampleFullName" sm={1}><i className="fa fa-user prefix"></i></Label>
                        <Col sm={11}>
                            <Input type="text" name="FullName" id="exampleFullName" placeholder="Full Name" onChange={props.changeEvent} value={props.FullName} />
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="examplePhone" sm={1}><i className="fas fa-phone"></i></Label>
                        <Col sm={11}>
                            <Input type="text" name="PhoneNum" id="examplePhone" placeholder="Phone Number" onChange={props.changeEvent} value={props.PhoneNumb} />
                        </Col>
                    </FormGroup>
                </Form>
            </ModalBody>
            <ModalFooter>
                <Button color="primary" onClick={props.Submit}>Submit</Button>{' '}
                <Button color="secondary" onClick={props.handleToggle}>Cancel</Button>
            </ModalFooter>
        </Modal>

    </div>

)

class HourBtn extends React.Component {
    state= {
        modal: false,
        booked: false,
        FullName: '',
        phone: ''
    }


    toggle = () => {
        this.setState({
          modal: !this.state.modal
        });
    }

    handleSubmit = (event) => {
      event.preventDefault();
      this.setState({booked: true})
    }

    handleChange = (event) => {
    this.setState({
        [event.target.name]: event.target.value
      })
 }

  render() {
    return (
      <Container className="DisplayHours">
          <Row >
          <Col xs="2" sm="2" md="2" lg="2" className="time">
            <ModalBtn Submit={this.handleSubmit} 
                      handleToggle={this.toggle}
                      modalState={this.state.modal} 
                      changeEvent={this.handleChange}
                      status={this.state.booked}
                      FullName={this.state.FullName} 
                      buttonLabel="9 AM" />
          </Col>

      </Row>
      </Container>

    );
  }
}

export default HourBtn;
