import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

const ModalBtn = (props) => (
    <div>
        <Button  onClick={props.handleToggle} className="rounded-circle hourBtn" id="btn-hr">{props.buttonLabel} </Button>
    <Modal isOpen={props.modalState} toggle={props.handleToggle} clas>
      <ModalHeader toggle={props.handleToggle}>Modal title</ModalHeader>
      <ModalBody>
        Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
      </ModalBody>
      <ModalFooter>
        <Button color="primary" onClick={props.handleSubmit}>Submit</Button>{' '}
        <Button color="secondary" onClick={props.handleToggle}>Cancel</Button>
      </ModalFooter>
    </Modal>

    </div>

)

class HourBtn extends React.Component {
    state= {
        modal: false
    }


  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  }

  render() {
    return (
      <div>
        <ModalBtn handleToggle={this.toggle} modalState={this.state.modal}/>
      </div>
    );
  }
}

export default HourBtn;
