import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Container, Row, Col, Form, FormGroup, Label, Input } from 'reactstrap';

const startTime = 9;
const endTime = 5;
const endTimeGMT = endTime + 12

let hours = [];

for(let i = startTime ; i < endTimeGMT + 1; i++ ) {
    if(i > 12 ) {
        hours.push(i - 12 + ` PM`)
    } else if(i < 12){
        hours.push(i + ` AM`)
    } else {
        hours.push(i + ` PM`)
    }

}

const ModalBtn = (props) => (
    <div>
        <Button  onClick={props.handleToggle} className="rounded-circle hourBtn" id="btn-hr">{props.buttonLabel} </Button>
    <Modal isOpen={props.modalState} toggle={props.handleToggle} className="modal-dialog-centered">
      <ModalHeader toggle={props.handleToggle}>Modal title</ModalHeader>
      <ModalBody>
        <Form>
        <FormGroup row>
          <Label for="exampleEmail" sm={2}>Email</Label>
          <Col sm={10}>
            <Input type="text" name="email" id="exampleEmail" placeholder="Full Name" />
          </Col>
        </FormGroup>
        <FormGroup row>
          <Label for="examplePassword" sm={2}>Password</Label>
          <Col sm={10}>
            <Input type="text" name="password" id="examplePassword" placeholder="password placeholder" />
          </Col>
        </FormGroup>
        </Form>
      </ModalBody>
      <ModalFooter>
        <Button color="primary" onClick={props.handleSubmit}>Submit</Button>{' '}
        <Button color="secondary" onClick={props.handleToggle}>Cancel</Button>
      </ModalFooter>
    </Modal>

    </div>

)

class HourBtn extends React.Component {
    state= {
        modal: false
    }


  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  }

  render() {
    return (
      <Container className="DisplayHours">
          <Row >
          <Col xs="2" sm="2" md="2" lg="2" className="time">
            <ModalBtn handleToggle={this.toggle} modalState={this.state.modal} buttonLabel="9 AM"/>
          </Col>

      </Row>
      </Container>

    );
  }
}

export default HourBtn;
