import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Container, Row, Col, Form, FormGroup, Label, Input } from 'reactstrap';



const ModalBtn = (props) => (
    <div>
        <Button  onClick={props.handleToggle} className={`rounded-circle hourBtn booked-${props.status}`} data-name={props.data}>{props.buttonLabel}</Button>

    </div>

)

