class HourBtn extends React.Component {
    state= {
        modal1: false,
        modal1: false,
        booked9: false,
        booked10: false,
        booked11: false,
        booked12: false,
        booked13: false,
        booked14: false,
        booked15: false,
        booked16: false,
        booked17: false,
        FullName9: '',
        FullName10: '',
        FullName11: '',
        FullName12: '',
        FullName13: '',
        FullName14: '',
        FullName15: '',
        FullName16: '',
        FullName17: '',
        PhoneNumb9: '',
        PhoneNumb10: '',
        PhoneNumb11: '',
        PhoneNumb12: '',
        PhoneNumb13: '',
        PhoneNumb14: '',
        PhoneNumb15: '',
        PhoneNumb16: '',
        PhoneNumb17: ''
    }

    toggle = nr => () => {
        let modalNumber = 'modal' + nr
        this.setState({
          [modalNumber]: !this.state[modalNumber]
        });
    }


    // toggle  = () => {
    //     this.setState({
    //       modal: !this.state.modal
    //     });
    // }


    submit9 = () => {
      this.setState({booked9: true})

    }

    submit10 = () => {
        this.setState({booked9: true})

    }

    submit11 = () => {
        this.setState({booked11: true})

    }


    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })

    }


    render() {
        return (
            <Container className="DisplayHours">
                <Row >
                    <Col xs="2" sm="2" md="2" lg="2" className="time">
                    <Button  onClick={this.toggle(1)} className={`rounded-circle hourBtn booked-${this.state.booked9}`} >9Am</Button>
                    <Modal isOpen={this.state.modal1} toggle={this.toggle(1)} className="modal-dialog-centered">
            <ModalHeader toggle={this.toggle(1)}>Schedule Appointment</ModalHeader>
            <ModalBody>
                <Form>
                    <FormGroup row>
                        <Label for="exampleFullName" sm={1}><i className="fa fa-user prefix"></i></Label>
                        <Col sm={11}>
                            <Input type="text" name="FullName9" id="exampleFullName" placeholder="Full Name" onChange={this.handleChange} value={this.state.FullName9} />
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="examplePhone" sm={1}><i className="fas fa-phone"></i></Label>
                        <Col sm={11}>
                            <Input type="text" name="PhoneNum9" id="examplePhone" placeholder="Phone Number" onChange={this.handleChange} value={this.state.PhoneNumb9} />
                        </Col>
                    </FormGroup>
                </Form>
            </ModalBody>
            <ModalFooter>
                <Button   color="primary" onClick={this.submit9}>Submit</Button>
                <Button color="secondary" onClick={this.toggle(1)}>Cancel</Button>

            </ModalFooter>
    </Modal>              
                    </Col>
                    <Col xs="2" sm="2" md="2" lg="2" className="time">
                            <ModalBtn data="btn-10"
                                      Submit={this.submit10} 
                                      handleToggle={this.toggle}
                                      modalState={this.state.modal1} 
                                      changeEvent={this.handleChange}
                                      status={this.state.booked10}
                                      name="FullName10"
                                      Phone="PhoneNumb10"
                                      FullName={this.state.FullName10}
                                      PhoneNumb={this.state.PhoneNumb10} 
                                      buttonLabel="10AM" />
                    </Col>
                    <Col xs="2" sm="2" md="2" lg="2" className="time">
                            <ModalBtn Submit={this.submit11} 
                                      handleToggle={this.toggle}
                                      modalState={this.state.modal} 
                                      changeEvent={this.handleChange}
                                      status={this.state.booked11}
                                      name="FullName11"
                                      Phone="PhoneNumb11"
                                      FullName={this.state.FullName11}
                                      PhoneNumb={this.state.PhoneNumb11} 
                                      buttonLabel="10AM" />
                    </Col>
                </Row>
            </Container>
    );
  }
}

export default HourBtn;
